import { Component } from "react";
import url from "url";
import SubCom from "../components/SubCom";

class Detail extends Component {
  constructor() {
    super();
    this.state = {
      users: [],
      isshow: true,
    };
  }
  render() {
    return (
      <div className="Detail">
        {/* <button onClick={this.back}>返回</button> */}
        <SubCom></SubCom>
        <h1 style={this.hstyle()}>我是Detail组件</h1>
        <h1 style={this.style()}>我是会显示或隐藏的标签</h1>
        <button onClick={this.setStyle}>动态样式</button>
      </div>
    );
  }
  style = () => {
    // 类似于 Vue 的计算属性，可以根据 state 中一些数据的变化，动态的计算某些标签的样式。
    return {
      display: this.state.isshow ? "block" : "none",
    };
  };
  hstyle = () => {
    return {
      color: this.state.isshow ? "black" : "blue",
    };
  };
  setStyle = () => {
    this.setState({
      isshow: !this.state.isshow,
    });
  };
  componentDidMount() {
    // 接收路由传参。
    // 1. ?传参如何获取  this.props.location.search
    // let { query } = url.parse(this.props.location.search, true);
    // console.log(query);

    // 2. 路径传参 /index/:id
    let id = this.props.match.params.id;
    console.log(id);

    // axios.get("xxx").then(res=>{
    //   this.setState({
    //     users: res.data
    //   })
    // })
  }
  // back = () => {
  //   // this.props.history.goBack();  // 返回上一页
  //   this.props.history.push("/regist");
  // };
}

export default Detail;
