import { Component } from "react";
import { Link, Switch, Route } from "react-router-dom";
import DetailOne from "./Detail1";
import DetailTwo from "./Detail2";

class Login extends Component {
  render() {
    return (
      <div className="login">
        <h1>我是Login组件</h1>
        {/* <Link to="/detail?id=1&name=张三">进入详情页</Link> */}
        <Link to="/detail/1">进入详情页</Link>
        <hr />
        {/* 这里设置的 Switch + Route 属于二级路由，嵌套路由，类似于 Vue 中的 {path: "/", children: [{path: "index"}]} */}
        <Link to="/login/detail1">登录的子路由1</Link>
        <Link to="/login/detail2">登录的子路由2</Link>
        <Switch>
          {/* /login 是一级路由的地址 /detail1 是二级路由地址。 */}
          <Route path="/login/detail1" component={DetailOne} />
          <Route path="/login/detail2" component={DetailTwo} />
        </Switch>
      </div>
    );
  }
}

export default Login;
