import "./App.css";
import { Component } from "react";
import { Switch, Route, Redirect, NavLink } from "react-router-dom";
import Login from "./views/Login";
import Regist from "./views/Regist";
import Detail from "./views/Detail";
import NotFound from "./views/NotFound";

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: "小明",
    };
  }
  render() {
    return (
      <div className="app">
        {/* <Link to="/login">登录</Link>&nbsp;&nbsp;&nbsp;&nbsp;
        <Link to="/regist">注册</Link> */}

        <NavLink to="/login">登录</NavLink>&nbsp;&nbsp;&nbsp;&nbsp;
        <NavLink to="/regist">注册</NavLink>
        <hr />
        {/* 使用 Switch 声明路由，它内部通常包裹 <Route>或者<Redirect>，这里的路由匹配规则，是按照自上而下的顺序匹配的。 */}
        {/* Switch 就相当于 vue 中的 <router-view /> 用于展示路由组件的。 */}
        {/* <Route> 和 <Redirect> 都包含一个 exact 属性，表示路由的严格匹配模式，默认情况下，React中的路由是模糊匹配规则。注意：如果一个一级路由包含子路由，那么这个一级路由不要使用严格模式 exact。如果一个一级路由没有子路由，可以使用 exact 。*/}
        <Switch>
          <Route path="/login" component={Login} />
          <Route path="/regist" component={Regist} />
          <Route path="/detail/:id" component={Detail} />

          {/* 设置地址的重定向 */}

          <Redirect exact from="/" to="/login"></Redirect>

          {/* 类似于 Vue 中的 {path: "*", component: NotFound}，当上面的路由匹配失败的时候，就自动匹配该路由。 */}
          <Route component={NotFound} />
        </Switch>
      </div>
    );
  }
  setName = () => {
    this.setState({
      name: "张三",
    });
  };
}

export default App;
